#!/bin/bash# start-api.sh - Startup script for the Real-ESRGAN API server# Set environment variable to indicate we're running in Dockerexport DOCKER_CONTAINER=1# Set the number of workers based on environment variable or default to 1WORKERS=${WORKERS:-1}# Print startup informationecho "Starting Real-ESRGAN API Server"echo "Workers: $WORKERS"echo "Python: $(python --version)"# Execute uvicorn with proper settingsexec uvicorn main:app --host 0.0.0.0 --port 8000 --workers $WORKERS